mapper.select:
SELECT order_id, user_id, order_no, amount, status, create_time, update_time
FROM orders
WHERE order_id = #{orderId}

SELECT o.order_id, o.user_id, o.order_no, o.amount, o.status, o.create_time, o.update_time
FROM orders o
INNER JOIN users u ON o.user_id = u.id


        AND o.user_id = #{userId}


        AND o.order_no LIKE CONCAT('%', #{orderNo}, '%')


        AND o.status = #{status}


        AND o.amount >= #{minAmount}


        AND o.amount <= #{maxAmount}


        AND o.create_time >= #{startTime}


        AND o.create_time <= #{endTime}


        AND u.username LIKE CONCAT('%', #{username}, '%')


ORDER BY o.create_time DESC

    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM orders o
INNER JOIN users u ON o.user_id = u.id


        AND o.user_id = #{userId}


        AND o.status = #{status}


        AND o.create_time >= #{startTime}


        AND o.create_time <= #{endTime}

SELECT
    COUNT(*) as total_orders,
    SUM(amount) as total_amount,
    AVG(amount) as avg_amount,
    MAX(amount) as max_amount,
    MIN(amount) as min_amount
FROM orders


        AND status = #{status}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}

mapper.update:
UPDATE orders
order_no = #{orderNo}, amount = #{amount}, status = #{status}, update_time = #{updateTime},
WHERE order_id = #{orderId}

UPDATE orders
SET status = #{status}, update_time = #{updateTime}
WHERE order_id IN

    #{orderId}

UPDATE orders
SET status = -1, update_time = #{updateTime}
WHERE order_id = #{orderId}
